import {React, useState, useEffect} from 'react';
import { FormControl, MenuItem,Select, Card, CardContent } from '@material-ui/core';
import './App.css';
import Table from './Table';
import { sortData } from './util.js';
import InfoBox from './InfoBox';
import LineGraph from './LineGraph';
import MapView from './MapView';

import "leaflet/dist/leaflet.css";

function App() {
  const [countries, setCountries] = useState([]);
  const [country, setCountry] = useState('Worldwide');
  const [countryInfo, setCountryInfo] = useState({});
  const [tableData, setTableData] = useState([]);
  const [mapCenter, setMapCenter] = useState({lat:34.80746, lng:-40.4796})
  const [mapZoom, setMapZoom] = useState(4);
  // https://disease.sh/v3/covid-19/countries

  //UseEffect-- runs a piece of code based on given condition

  useEffect(()=>{
    fetch("https://disease.sh/v3/covid-19/all")
    .then((response) => response.json())
    .then((data) =>{
      setCountryInfo(data)
    });

  },[]);

  useEffect(() =>{
    //async -> sends a request , wait for its response.
    const getCountriesData = async () =>{
      await fetch("https://disease.sh/v3/covid-19/countries")
      .then((response)=>response.json())
      .then((data) => {
        const countries = data.map((country) =>(
          {
            name: country.country,
            value: country.countryInfo.iso2,
          }
        ));

        setCountries(countries); // dropdown
        const sortedData = sortData(data); // table data.. whole data is passed in sortData.
        setTableData(sortedData);
      })
    };
    getCountriesData();
  },[]);

  const onCountryChange = async(event) =>{
    const countryCode = event.target.value;

    const url = countryCode ==='worldwide'? 'https://disease.sh/v3/covid-19/all' 
    :`https://disease.sh/v3/covid-19/countries/${countryCode}` ;

    await fetch(url)
    .then(response => response.json())
    .then(data =>{
      setCountry(countryCode);
      setCountryInfo(data);

      setMapCenter([data.country.lat, data.country.long])
    })

  }


  return (
    <div className="app">
      <div className="app__left">
      <div className ="app__header">
      <h1>COVID-19 TRACKER</h1>
      <FormControl className="app__dropdown">
        <Select
          variant = "outlined"
          onChange={onCountryChange}
          value={country}>
            <MenuItem value="Worldwide">Worldwide</MenuItem>
            {
              countries.map((country) => (
                <MenuItem value={country.value}>{country.name}</MenuItem>
              ))
            }
        </Select>

        </FormControl>

      </div>
      <div className="app__stats">
        <InfoBox title="COVID-19 Cases" cases={countryInfo.todayCases} total={countryInfo.cases}/>

        <InfoBox title="Recovered" cases={countryInfo.todayRecovered} total={countryInfo.recovered}/>

        <InfoBox title="Deaths" cases={countryInfo.todayDeaths} total={countryInfo.deaths}/>

      </div>



      {/* Table */}
      {/* Graph */}
      <div className="app__map">
        <MapView center={mapCenter}
        zoom = {mapZoom} />

      </div>
      </div>
      <Card className="app__right">
        <CardContent>
          <h3>Live Cases by Country</h3>
          <Table countries={tableData}/>
          <h3> Worldwide cases</h3>
        </CardContent>
        <LineGraph/>
      </Card>


    </div>
  );
}

export default App;
